import React from 'react';
import './newuserlogin.css';
import axios from 'axios';
import { useState } from 'react';



export default class NewUserLogin extends React.Component {
    state = {
        username: '',
        password: '',
        email: '',
        first_name: '',
        last_name: '',
        middle_name: '',
      }
    
      handleChange = event => {
        this.setState({ 
            [event.target.name] : event.target.value
        });
      }
          
    
      handleSubmit = async event => {
        event.preventDefault();
         
    
        await axios ({
            method:"post", 
            url:"http://127.0.0.1:8000/api/auth/register/",
            data:{  
                "username": this.state.username, 
                "password": this.state.password,  
                "email": this.state.email,     
                "first_name": this.state.first_name,
                "last_name": this.state.last_name,
                "middle_name": this.state.middle_name
                
                
            }
        })
        window.location="/login"
      }
    
render(){
return (
            
                <div class="form-group">
                <center><h3>Create 🆕 User</h3></center>
                    <form onSubmit={this.handleSubmit}>
                    <div>    
                    <center><label  for="username">Enter User Name:</label><input name="username" type="text" class="form-control" value={this.state.username} onChange={this.handleChange}></input></center>
                    </div>
                    <br />
                    <div>
                    <center><label  for="password">Create Password:</label><input name="password" type="password" class="form-control" value={this.state.password} onChange={this.handleChange}></input></center>
                    </div>
                    <br />
                    <br />
                    <div>
                    <center><label  for="email">Enter Email Address:</label><input type="email" class="form-control" name="email" placeholder='Enter Email' value={this.state.email} onChange={this.handleChange}></input></center>
                    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>
                    <br />
                    <div>
                    <center><label  for="first_name">Enter First Name:</label><input name="first_name" type="text" class="form-control" value={this.state.first_name} onChange={this.handleChange}></input></center>
                    </div>
                    <br />
                    <div>
                    <center><label  for="last_name">Enter Last Name:</label><input name="last_name" type="text" class="form-control" value={this.state.last_name} onChange={this.handleChange}></input></center>
                    </div>
                    <br />
                    <div>
                    <center><label  for="middle_name">Enter Middle Name:</label><input name="middle_name" type="text" class="form-control" value={this.state.middle_name} onChange={this.handleChange}></input></center>
                    </div>
                    <br />
                    <center><button type="submit" class="btn btn-primary">Submit</button></center>
                    </form>
                    </div>
           
        )
    }    
}
