import ReactDOM from 'react-dom';
import React from "react";
import './multiplechoice.css'










  
export default class MultipleChoice extends React.Component{
    // state = {
    //     username: '',
    //     password: '',
    //     email: '',
    //     first_name: '',
    //     last_name: '',
    //     middle_name: '',
    //   }
    
    //   handleChange = event => {
    //     this.setState({ 
    //         [event.target.name] : event.target.value
    //     });
    //   }
     
     submitAnswer=async event=> {
        event.preventDefault();

        var radios = document.getElementsByName('choice');
    var val= "";
    for (var i = 0, length = radios.length; i < length; i++) {
        if (radios[i].checked) {
           val = radios[i].value; 
           break;
         }
        }
    if (val === "" ) {
      alert('please select choice answer');
    } else if ( val === "Scripting" ) {
      alert('Answer is correct !');
    } else {
      alert('Answer is wrong');
    }
    }

    
    
    
  
render(){
    return(
        <div>
            <h1>JavaScript is ______ Language.</h1>
            
  <form >
  <input type="radio" onSubmit={this.submitAnswer} className='choice' value="Scripting"/> Scripting
  <input type="radio" onSubmit={this.submitAnswer} className='choice' value="Programming"/> Programming
  <input type="radio" onSubmit={this.submitAnswer} className='choice'value="Application"/> Application
  <input type="radio" onSubmit={this.submitAnswer} className='choice' value="None of These"/> None of These
  
  </form>
  <button onClick={this.submitAnswer}>Submit Answer</button>
        </div>
    )
}
}

const rootElement = document.getElementById('root')
ReactDOM.render(<MultipleChoice></MultipleChoice>, rootElement)
