import ReactDOM from 'react-dom';
import React from "react";
import './multiplechoice.css'










  
  function MultipleChoice(){
     

    var radios = document.getElementsByName('choice');
    var val= "";
    for (var i = 0, length = radios.length; i < length; i++) {
        if (radios[i].checked) {
           val = radios[i].value; 
           break;
         }
    
    
    if (val === "" ) {
      alert('please select choice answer');
    } else if ( val === "Scripting" ) {
      alert('Answer is correct !');
    } else {
      alert('Answer is wrong');
    }
  };
    return(
        <div>
            <h1>JavaScript is ______ Language.</h1>
            
  <form >
  <input type="radio" name="choice" value="Scripting"> Scripting</input>
  <input type="radio" name="choice" value="Programming"> Programming</input>
  <input type="radio" name="choice" value="Application"> Application</input>
  <input type="radio" name="choice" value="None of These"> None of These</input>
  <input type="submit" />
  </form>
  <button></button>
        </div>
    )
}

export default MultipleChoice

