import React from 'react';
import './newuserlogin.css';
import axios from 'axios';
import { useState } from 'react';



export default class NewUserLogin extends React.Component {
    state = {
        username: '',
        password: '',
        email: '',
        first_name: '',
        last_name: '',
        middle_name: '',
      }
    
      handleChange = event => {
        this.setState({ 
            [event.target.name] : event.target.value
        });
      }
          
    
      handleSubmit = async event => {
        event.preventDefault();
         
    
        await axios ({
            method:"post", 
            url:"http://127.0.0.1:8000/api/auth/register/",
            data:{  
                "username": this.state.username, 
                "password": this.state.password,  
                "email": this.state.email,     
                "first_name": this.state.first_name,
                "last_name": this.state.last_name,
                "middle_name": this.state.middle_name
                
                
            }
        })
        window.location="/login"
      }
    
render(){
return (
    <body>
        
    
            
                <center>   <div class="form-group">
                    <h3>Create New User</h3>
                    <form onSubmit={this.handleSubmit}>
                    <div class="form-group">    
                    <label  for="username">User Name:</label><br />
                    <input name="username" type="text" class="form-control" value={this.state.username} onChange={this.handleChange}></input>
                    </div>
                    
                    <div class="form-group">
                    <label  for="password">Create Password:</label><br />
                    <input name="password" type="password" class="form-control" value={this.state.password} onChange={this.handleChange}></input>
                    </div>
                   
                    <div class="form-group">
                    <label  for="email">Email Address:</label><br />
                    <input type="email" class="form-control" name="email" placeholder='Enter Email' value={this.state.email} onChange={this.handleChange}></input>
                    <br />
                    
                    </div>
                    
                    <div class="form-group">
                    <label  for="first_name">First Name:</label><br />
                    <input name="first_name" type="text" class="form-control" value={this.state.first_name} onChange={this.handleChange}></input>
                    </div>
                    
                    <div class="form-group">
                    <label  for="last_name">Last Name:</label><br />
                    <input name="last_name" type="text" class="form-control" value={this.state.last_name} onChange={this.handleChange}></input>
                    </div>
                    
                    <div class="form-group">
                    <label  for="middle_name">Middle Name:</label><br />
                    <input name="middle_name" type="text" class="form-control" value={this.state.middle_name} onChange={this.handleChange}></input>
                    </div>
                    
                    <button type="submit" class="btn btn-primary">Submit</button>
                    </form>
                    </div></center> 
                    
           </body>
        )
    }    
}
